    private void btnEditActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnEditActionPerformed
        if ("".equals(txtNama.getText()) || "".equals(txtAlamat.getText()) || "".equals(txtrasa.getText())
            || "".equals(txtTelp.getText()) || "".equals(txtpsn.getText())){
            JOptionPane.showMessageDialog(this, "Harap Lengkapi Data", "Error", JOptionPane.WARNING_MESSAGE);
        } else {
            String RS = "";
            if (rdVnl.isSelected())
            {
                RS = "L";
            } else {
                RS = "P";
            }
            String SQL = "UPDATE t_siswa SET "
            + "WHERE Nama='"+txtNama.getText()+"',"+ "WHERE Kelas='"+txtTelp.getText()+"',"
            + "WHERE Telp='"+RS+"',"+ "WHERE Email='"+txtrasa.getText()+"'"
            + "WHERE Alamat='"+txtpsn.getText()+"'"
            + "WHERE NIS='"+txtAlamat.getText()+"'";
            int status = KoneksiDB.execute(SQL);
            if (status == 0)
            {
                JOptionPane.showMessageDialog(this, "Data berhasil diupdate", "Sukses", JOptionPane.INFORMATION_MESSAGE);
                selectData();
            } else {
                JOptionPane.showMessageDialog(this, "Data gagal diupdate", "Gagal", JOptionPane.WARNING_MESSAGE);
            }
        }

        // TODO add your handling code here:
    }//GEN-LAST:event_btnEditActionPerformed

    private void btnAddActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAddActionPerformed
        if ("".equals(txtNama.getText()) || "".equals(txtAlamat.getText()) || "".equals(txtrasa.getText())
            || "".equals(txtTelp.getText()) || "".equals(txtpsn.getText())){
            JOptionPane.showMessageDialog(this, "Harap Lengkapi Data", "Error", JOptionPane.WARNING_MESSAGE);
        } else {
            String RS = "";
            if (rdVnl.isSelected())
            {
                RS = "Vanilla";
            } else {
                RS = "Chocolate";
            }
            String SQL = "INSERT INTO t_siswa (NIS, NamaSiswa, JenisKelamin, Kelas, Email, Alamat)"
            + "VALUES ('"+txtNama.getText()+"','"+txtTelp.getText()+"','"+RS+"',"
            + "'"+txtrasa.getText()+"','"+txtpsn.getText()+"','"+txtAlamat.getText()+"')";
            int status = KoneksiDB.execute(SQL);
            if (status == 1)
            {
                JOptionPane.showMessageDialog(this, "Data berhasil ditambahkan", "Sukses", JOptionPane.INFORMATION_MESSAGE);
                selectData();
            } else {
                JOptionPane.showMessageDialog(this, "Data gagal ditambahkan", "Gagal", JOptionPane.WARNING_MESSAGE);
            }
        }
        // TODO add your handling code here:
    }//GEN-LAST:event_btnAddActionPerformed

    private void btnDeleteActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnDeleteActionPerformed
        int baris = tblData.getSelectedRow();
        if (baris != -1) {
            String NIS = tblData.getValueAt(baris, 0).toString();
            String SQL = "DELETE FROM t_siswa WHERE NIS='"+NIS+"'";
            int status = KoneksiDB.execute(SQL);
            if (status==1) {
                JOptionPane.showMessageDialog(this, "Data berhasil dihapus", "Sukses", JOptionPane.INFORMATION_MESSAGE);
            } else {
                JOptionPane.showMessageDialog(this, "Data gagal dihapus", "GAgal", JOptionPane.WARNING_MESSAGE);
            }
        } else {
            JOptionPane.showMessageDialog(this, "Pilih Baris Data Terlebih Dahulu", "Error", JOptionPane.WARNING_MESSAGE);
        }

        // TODO add your handling code here:
    }//GEN-LAST:event_btnDeleteActionPerformed

