    private void tblDataMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_tblDataMouseClicked
        int baris = tblData.getSelectedRow();
        if (baris != -1) {
            txtNama.setText(tblData.getValueAt(baris, 0).toString());
            txtTelp.setText(tblData.getValueAt(baris,1).toString());
            if ("Vanilla".equals(tblData.getValueAt(baris, 2).toString())) {
                rdVnl.setSelected(true);
            }
            else {
                rdClt.setSelected(true);
            }
            txtrasa.setText(tblData.getValueAt(baris, 3).toString());
            txtpsn.setText(tblData.getValueAt(baris, 4).toString());
            txtAlamat.setText(tblData.getValueAt(baris, 5).toString());
        }
        // TODO add your handling code here:
    }//GEN-LAST:event_tblDataMouseClicked

